/**
 * Cuenta las palabras contenidas dentro del texto
 * 
 * @param texto
 *            string donde buscar la palabra
 * @param busqueda
 *            string palabra que buscamos dentro del texto
 * @return int 0 si no encuentra, numero de palabras encontradas
 *
 */
public class UtilidadesString {

	// TODO Auto-generated method stub

	static int buscarpalabra(String texto, String busqueda) {
		int resul = -1;

		if (texto == null || busqueda == null) {
			resul = 0;
		} else {			
			resul = 99;
		}

		return resul;
	}

}
